import { MessageChannel } from './channel.js';

export class FakeChannel extends MessageChannel {
	private _isDestroyed = false;
	protected readonly _delay: number = 5000;

	constructor() {
		super();
		this._startEmitting();
	}

	protected async _startEmitting() {
		while (!this._isDestroyed) {
			const timeout = this._delay - Date.now() % this._delay;
			await new Promise(r => setTimeout(r, timeout));
			if (this._isDestroyed) {
				return;
			}
			const offset = Math.round(Date.now() / this._delay);
			const message = this._fakeMessage(offset);
			this.queue(message, String(offset));
		}
	}

	protected _fakeMessage(offset: number): string {
		return JSON.stringify({
			'current-date': new Date().toISOString(),
			'random-number': Math.random(),
		}, null, 2);
	}

	public destroy() {
		this._isDestroyed = true;
		super.destroy();
	}
}

export class VanillaFakeChannel extends FakeChannel {
	protected readonly _delay = 3000;
	protected _fakeMessage(offset: number) {
		return vanilla[offset % vanilla.length];
	}
}

export class MotivationalFakeChannel extends FakeChannel {
	protected _fakeMessage(offset: number) {
		return motivational[offset % motivational.length];
	}
}


